package JavalectureNexttech;

public class Lec2forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	//increment, i++ or i=i+1 means increment by one
	// increment by w 2 means i+=2 or i=i+2
	// increment by 3 means i+=3 or i=i+3
	
	int a=10;
	a=a+2;
	System.out.println(a);
	
	
	//example of print value 0 to 9
	
	for(int i=0;i<=20;i=i+2) {
		System.out.println("The even numbers are="+i);
		
	}
	
	//print odd numbers from 4 to 30
	
	for (int j=5;j<=30;j=j+2) 
	{
		System.out.println(j);
	}
	
	
	
			

}}
